jQuery(document).ready(function(e){e.fn.offScreen=function(t){var n=e(this),r=e(window),i=r.scrollTop(),s=i+r.height(),o=n.offset().top-t,u=n.offset().top+n.height()+t;return{top:u<=i,bottom:o>=s}};var t=e(window),n=e(".module");n.each(function(t,n){var n=e(n);n.offScreen(150).bottom||n.addClass("already-visible")});t.on("scroll resize",function(t){n.each(function(t,n){var n=e(n);if(!n.offScreen(150).top&&!n.offScreen(150).bottom){n.removeClass("already-visible off-screen-top off-screen-bottom");n.addClass("come-in")}else n.offScreen(150).top?n.addClass("off-screen-top"):n.addClass("off-screen-bottom")})});t.trigger("scroll")});